	function rebrandFunc() {
		try {
			var oldTxt = "trusted since 1901";
			var newTxt = "at the corner of happy & healthy";
			for (var i of ["a", "img"]) {
				for (var j of document.getElementsByTagName(i)) {
					var title = j.title || "";
					if (title && title.indexOf(oldTxt) != -1) {
						j.setAttribute("title", title.replace(oldTxt, newTxt));
					}
					var alt = j.alt || "";
					if (alt && alt.indexOf("trusted since 1901") != -1) {
						j.setAttribute("alt", alt.replace(oldTxt, newTxt));
					}
					var txt = j.innerHTML || "";
					if (txt && txt.indexOf("trusted since 1901") != -1) {
						j.innerHTML = txt.replace(oldTxt, newTxt);
					}
				}
			}
		} catch (err) {
			console.log("An error has occurred: " + err.message);
		}
	}
	
	ready(rebrandFunc);

